package examples.simple;

/** Demonstrates variables of different types */
public class Types {
  public static void main(String[] args) {
    // Primitive types
    char letterA = 'a';
    byte largestByte = 127;
    short largestShort = 32_767;
    int largestInt = 2_147_483_647;
    long largestLong = 9_223_372_036_854_775_807L;
    float largestFloat = 340282346638528860000000000000000000000.0f;
    double largestDouble = .0;
    double smallestDouble = 0.;
    boolean isSummer = true;

    // Reference types
    String heroName = "Thor";

    // Arrays
    double[] dailyTemperatures = {22.0, 25.1, 30, 17.4};
  }
}
